import React from 'react';
import { getCredential } from './webauthn';
import './Login.css';

const Login = () => {
    const handleLogin = async () => {
        const assertion = await getCredential();
        console.log('Assertion:', assertion);

        const response = await fetch('https://localhost/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id: assertion.id,
                rawId: assertion.rawId,
                response: {
                    authenticatorData: assertion.response.authenticatorData,
                    clientDataJSON: assertion.response.clientDataJSON,
                    signature: assertion.response.signature,
                    userHandle: assertion.response.userHandle,
                },
                type: assertion.type,
            }),
        });

        if (response.ok) {
            console.log('Logged in successfully');
        } else {
            console.error('Login failed');
        }
    };

    return (
        <div className="auth-container">
            <img src="kwaai.png" alt="Kwaai Logo" className="logo" />
            <h1>Login</h1>
            <input type="email" placeholder="Email" className="input-field" />
            <button onClick={handleLogin} className="auth-button">Login with Passkey</button>
            <p className="auth-link">Don't have an account? <a href="/register">Register here</a></p>
        </div>
    );
};

export default Login;
