// webauthn.js
export const createCredential = async () => {
    const publicKeyCredentialCreationOptions = {
        challenge: Uint8Array.from('randomStringFromServer', c => c.charCodeAt(0)),
        rp: {
            name: "Kwaai"
        },
        user: {
            id: Uint8Array.from('uniqueUserId', c => c.charCodeAt(0)),
            name: "user@example.com",
            displayName: "User"
        },
        pubKeyCredParams: [
            {
                type: "public-key",
                alg: -7 // ES256 algorithm
            }
        ],
        authenticatorSelection: {
            authenticatorAttachment: "cross-platform", // Ensure cross-platform for QR code
            userVerification: "preferred"
        },
        timeout: 60000,
        attestation: "direct"
    };

    const credential = await navigator.credentials.create({
        publicKey: publicKeyCredentialCreationOptions
    });

    return credential;
};

export const getCredential = async () => {
    const publicKeyCredentialRequestOptions = {
        challenge: Uint8Array.from('randomStringFromServer', c => c.charCodeAt(0)),
        allowCredentials: [
            {
                type: "public-key",
                id: Uint8Array.from('credentialIdFromServer', c => c.charCodeAt(0)),
                transports: ["internal", "cross-platform", "hybrid"] // Ensure cross-platform and hybrid for QR code
            }
        ],
        userVerification: "preferred",
        timeout: 60000
    };

    const assertion = await navigator.credentials.get({
        publicKey: publicKeyCredentialRequestOptions
    });

    return assertion;
};
