// webauthn.js
export const createCredential = async (email) => {
    const publicKeyCredentialCreationOptions = {
        challenge: window.crypto.getRandomValues(new Uint8Array(32)),
        rp: {
            name: "Kwaai"
        },
        user: {
            id: window.crypto.getRandomValues(new Uint8Array(32)),
            name: email,
            displayName: email
        },
        pubKeyCredParams: [
            {
                type: "public-key",
                alg: -7 // ES256 algorithm
            }
        ],
        authenticatorSelection: {
            authenticatorAttachment: "hybrid", // Ensure cross-platform for QR code
            userVerification: "preferred"
        },
        timeout: 60000,
        attestation: "direct"
    };

    console.log('PublicKeyCredentialCreationOptions:', publicKeyCredentialCreationOptions);

    try {
        console.log('Creating credential...');
        const credential = await navigator.credentials.create({
            publicKey: publicKeyCredentialCreationOptions
        });
        console.log('Credential created:', credential);
        return credential;
    } catch (error) {
        console.error('Error creating credential:', error);
        throw error;
    }
};

export const getCredential = async (credentialId) => {
    const publicKeyCredentialRequestOptions = {
        challenge: window.crypto.getRandomValues(new Uint8Array(32)),
        allowCredentials: [
            {
                type: "public-key",
                id: Uint8Array.from(atob(credentialId), c => c.charCodeAt(0)),
                transports: ["internal", "cross-platform"] // Ensure cross-platform for QR code
            }
        ],
        userVerification: "preferred",
        timeout: 60000
    };

    try {
        const assertion = await navigator.credentials.get({
            publicKey: publicKeyCredentialRequestOptions
        });
        return assertion;
    } catch (error) {
        console.error('Error getting credential:', error);
        throw error;
    }
};
